<?php\nheader('Content-Type: application/json');\n\n$dbPath = __DIR__ . '/../../data/talcual.db';\nif (!file_exists($dbPath)) {\n    http_response_code(500);\n    echo json_encode(['error' => 'Database not initialized']);\n    exit;\n}\n\n$pdo = new PDO('sqlite:' . $dbPath);\n$pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n$pdo->exec('PRAGMA foreign_keys = ON');\n\n$action = $_GET['action'] ?? $_POST['action'] ?? '';\n\ntry {\n    if ($action === 'list') {\n        $stmt = $pdo->query('SELECT * FROM categories ORDER BY orden ASC, id ASC');\n        echo json_encode($stmt->fetchAll(PDO::FETCH_ASSOC));\n    } elseif ($action === 'get') {\n        $id = $_GET['id'] ?? null;\n        $stmt = $pdo->prepare('SELECT * FROM categories WHERE id = ?');\n        $stmt->execute([$id]);\n        echo json_encode($stmt->fetch(PDO::FETCH_ASSOC));\n    } elseif ($action === 'create') {\n        $data = json_decode(file_get_contents('php://input'), true);\n        $name = $data['name'] ?? '';\n        $orden = $data['orden'] ?? 0;\n\n        if (!$name) {\n            throw new Exception('Name is required');\n        }\n\n        $stmt = $pdo->prepare('INSERT INTO categories (name, orden, date) VALUES (?, ?, ?)');\n        $stmt->execute([$name, $orden, time()]);\n        echo json_encode(['id' => $pdo->lastInsertId()]);\n    } elseif ($action === 'batch_create') {\n        $data = json_decode(file_get_contents('php://input'), true);\n        $categories = $data['categories'] ?? [];\n\n        $pdo->beginTransaction();\n        $inserted = 0;\n        foreach ($categories as $cat) {\n            try {\n                $stmt = $pdo->prepare('INSERT INTO categories (name, orden, date) VALUES (?, ?, ?)');\n                $stmt->execute([$cat['name'], $cat['orden'] ?? 0, time()]);\n                $inserted++;\n            } catch (PDOException $e) {\n            }\n        }\n        $pdo->commit();\n        echo json_encode(['inserted' => $inserted, 'total' => count($categories)]);\n    } elseif ($action === 'update') {\n        $data = json_decode(file_get_contents('php://input'), true);\n        $id = $data['id'] ?? null;\n        $name = $data['name'] ?? '';\n        $orden = $data['orden'] ?? 0;\n        $is_active = $data['is_active'] ?? 1;\n\n        if (!$id) {\n            throw new Exception('ID is required');\n        }\n\n        $stmt = $pdo->prepare('UPDATE categories SET name = ?, orden = ?, is_active = ? WHERE id = ?');\n        $stmt->execute([$name, $orden, $is_active, $id]);\n        echo json_encode(['success' => true]);\n    } elseif ($action === 'delete') {\n        $id = $_POST['id'] ?? null;\n        if (!$id) {\n            throw new Exception('ID is required');\n        }\n\n        $stmt = $pdo->prepare('DELETE FROM categories WHERE id = ?');\n        $stmt->execute([$id]);\n        echo json_encode(['success' => true]);\n    } elseif ($action === 'reorder') {\n        $data = json_decode(file_get_contents('php://input'), true);\n        $items = $data['items'] ?? [];\n\n        $pdo->beginTransaction();\n        foreach ($items as $idx => $item) {\n            $stmt = $pdo->prepare('UPDATE categories SET orden = ? WHERE id = ?');\n            $stmt->execute([$idx, $item['id']]);\n        }\n        $pdo->commit();\n        echo json_encode(['success' => true]);\n    } else {\n        throw new Exception('Unknown action');\n    }\n} catch (Exception $e) {\n    http_response_code(400);\n    echo json_encode(['error' => $e->getMessage()]);\n}\n