<?php\nheader('Content-Type: application/json');\n\n$dbPath = __DIR__ . '/../../data/talcual.db';\nif (!file_exists($dbPath)) {\n    http_response_code(500);\n    echo json_encode(['error' => 'Database not initialized']);\n    exit;\n}\n\n$pdo = new PDO('sqlite:' . $dbPath);\n$pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n$pdo->exec('PRAGMA foreign_keys = ON');\n\n$action = $_GET['action'] ?? $_POST['action'] ?? '';\n\ntry {\n    if ($action === 'list') {\n        $status = $_GET['status'] ?? null;\n        if ($status) {\n            $stmt = $pdo->prepare('SELECT * FROM games WHERE status = ? ORDER BY created_at DESC');\n            $stmt->execute([$status]);\n        } else {\n            $stmt = $pdo->query('SELECT * FROM games ORDER BY created_at DESC');\n        }\n        echo json_encode($stmt->fetchAll(PDO::FETCH_ASSOC));\n    } elseif ($action === 'get') {\n        $id = $_GET['id'] ?? null;\n        $stmt = $pdo->prepare('SELECT * FROM games WHERE id = ?');\n        $stmt->execute([$id]);\n        $game = $stmt->fetch(PDO::FETCH_ASSOC);\n\n        if ($game) {\n            $game['metadata'] = json_decode($game['metadata'] ?? '{}', true);\n            $stmt = $pdo->prepare('\n                SELECT id, name, aura, status, score, round_history FROM players\n                WHERE game_id = ?\n                ORDER BY id ASC\n            ');\n            $stmt->execute([$id]);\n            $players = $stmt->fetchAll(PDO::FETCH_ASSOC);\n            foreach ($players as &$p) {\n                $p['round_history'] = json_decode($p['round_history'] ?? '{}', true);\n            }\n            $game['players'] = $players;\n        }\n\n        echo json_encode($game);\n    } elseif ($action === 'update') {\n        $data = json_decode(file_get_contents('php://input'), true);\n        $id = $data['id'] ?? null;\n\n        if (!$id) {\n            throw new Exception('ID is required');\n        }\n\n        $updates = [];\n        $values = [];\n\n        $allowedFields = ['status', 'round', 'round_starts_at', 'round_ends_at', 'updated_at'];\n        foreach ($allowedFields as $field) {\n            if (isset($data[$field])) {\n                $updates[] = "$field = ?";\n                $values[] = $data[$field];\n            }\n        }\n\n        if (empty($updates)) {\n            throw new Exception('No fields to update');\n        }\n\n        $values[] = $id;\n        $sql = 'UPDATE games SET ' . implode(', ', $updates) . ' WHERE id = ?';\n        $stmt = $pdo->prepare($sql);\n        $stmt->execute($values);\n\n        echo json_encode(['success' => true]);\n    } elseif ($action === 'delete') {\n        $id = $_POST['id'] ?? null;\n        if (!$id) {\n            throw new Exception('ID is required');\n        }\n\n        $stmt = $pdo->prepare('DELETE FROM games WHERE id = ?');\n        $stmt->execute([$id]);\n        echo json_encode(['success' => true]);\n    } else {\n        throw new Exception('Unknown action');\n    }\n} catch (Exception $e) {\n    http_response_code(400);\n    echo json_encode(['error' => $e->getMessage()]);\n}\n