<?php\nheader('Content-Type: application/json');\n\n$dbPath = __DIR__ . '/../../data/talcual.db';\nif (!file_exists($dbPath)) {\n    http_response_code(500);\n    echo json_encode(['error' => 'Database not initialized']);\n    exit;\n}\n\n$pdo = new PDO('sqlite:' . $dbPath);\n$pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n$pdo->exec('PRAGMA foreign_keys = ON');\n\n$action = $_GET['action'] ?? $_POST['action'] ?? '';\n\ntry {\n    if ($action === 'list') {\n        $gameId = $_GET['game_id'] ?? null;\n        if ($gameId) {\n            $stmt = $pdo->prepare('\n                SELECT id, game_id, name, aura, status, score, round_history, last_heartbeat\n                FROM players\n                WHERE game_id = ?\n                ORDER BY id ASC\n            ');\n            $stmt->execute([$gameId]);\n        } else {\n            $stmt = $pdo->query('\n                SELECT id, game_id, name, aura, status, score, round_history, last_heartbeat\n                FROM players\n                ORDER BY game_id ASC, id ASC\n            ');\n        }\n\n        $players = $stmt->fetchAll(PDO::FETCH_ASSOC);\n        foreach ($players as &$p) {\n            $p['round_history'] = json_decode($p['round_history'] ?? '{}', true);\n        }\n\n        echo json_encode($players);\n    } elseif ($action === 'get') {\n        $id = $_GET['id'] ?? null;\n        $gameId = $_GET['game_id'] ?? null;\n\n        if (!$id || !$gameId) {\n            throw new Exception('ID and game_id are required');\n        }\n\n        $stmt = $pdo->prepare('\n            SELECT * FROM players\n            WHERE id = ? AND game_id = ?\n        ');\n        $stmt->execute([$id, $gameId]);\n        $player = $stmt->fetch(PDO::FETCH_ASSOC);\n\n        if ($player) {\n            $player['round_history'] = json_decode($player['round_history'] ?? '{}', true);\n        }\n\n        echo json_encode($player);\n    } elseif ($action === 'update') {\n        $data = json_decode(file_get_contents('php://input'), true);\n        $id = $data['id'] ?? null;\n        $gameId = $data['game_id'] ?? null;\n\n        if (!$id || !$gameId) {\n            throw new Exception('ID and game_id are required');\n        }\n\n        $updates = [];\n        $values = [];\n\n        $allowedFields = ['name', 'aura', 'status', 'score', 'round_history', 'last_heartbeat'];\n        foreach ($allowedFields as $field) {\n            if (isset($data[$field])) {\n                $updates[] = "$field = ?";\n                $value = $data[$field];\n                if ($field === 'round_history' && is_array($value)) {\n                    $value = json_encode($value);\n                }\n                $values[] = $value;\n            }\n        }\n\n        if (empty($updates)) {\n            throw new Exception('No fields to update');\n        }\n\n        $values[] = $id;\n        $values[] = $gameId;\n        $sql = 'UPDATE players SET ' . implode(', ', $updates) . ' WHERE id = ? AND game_id = ?';\n        $stmt = $pdo->prepare($sql);\n        $stmt->execute($values);\n\n        echo json_encode(['success' => true]);\n    } else {\n        throw new Exception('Unknown action');\n    }\n} catch (Exception $e) {\n    http_response_code(400);\n    echo json_encode(['error' => $e->getMessage()]);\n}\n