<?php\nheader('Content-Type: application/json');\n\n$dbPath = __DIR__ . '/../../data/talcual.db';\nif (!file_exists($dbPath)) {\n    http_response_code(500);\n    echo json_encode(['error' => 'Database not initialized']);\n    exit;\n}\n\n$pdo = new PDO('sqlite:' . $dbPath);\n$pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n$pdo->exec('PRAGMA foreign_keys = ON');\n\n$action = $_GET['action'] ?? $_POST['action'] ?? '';\n\ntry {\n    if ($action === 'list') {\n        $categoryId = $_GET['category_id'] ?? null;\n        if ($categoryId) {\n            $stmt = $pdo->prepare('\n                SELECT DISTINCT p.* FROM prompts p\n                INNER JOIN prompt_categories pc ON p.id = pc.prompt_id\n                WHERE pc.category_id = ?\n                ORDER BY p.id ASC\n            ');\n            $stmt->execute([$categoryId]);\n        } else {\n            $stmt = $pdo->query('SELECT * FROM prompts ORDER BY id ASC');\n        }\n        echo json_encode($stmt->fetchAll(PDO::FETCH_ASSOC));\n    } elseif ($action === 'get') {\n        $id = $_GET['id'] ?? null;\n        $stmt = $pdo->prepare('SELECT * FROM prompts WHERE id = ?');\n        $stmt->execute([$id]);\n        $prompt = $stmt->fetch(PDO::FETCH_ASSOC);\n        if ($prompt) {\n            $stmt = $pdo->prepare('SELECT category_id FROM prompt_categories WHERE prompt_id = ?');\n            $stmt->execute([$id]);\n            $prompt['categories'] = $stmt->fetchAll(PDO::FETCH_COLUMN);\n        }\n        echo json_encode($prompt);\n    } elseif ($action === 'create') {\n        $data = json_decode(file_get_contents('php://input'), true);\n        $text = $data['text'] ?? '';\n        $difficulty = $data['difficulty'] ?? 1;\n        $categories = $data['categories'] ?? [];\n\n        if (!$text) {\n            throw new Exception('Text is required');\n        }\n\n        $stmt = $pdo->prepare('INSERT INTO prompts (text, difficulty, date) VALUES (?, ?, ?)');\n        $stmt->execute([$text, $difficulty, time()]);\n        $promptId = $pdo->lastInsertId();\n\n        foreach ($categories as $catId) {\n            $stmt = $pdo->prepare('INSERT INTO prompt_categories (prompt_id, category_id) VALUES (?, ?)');\n            $stmt->execute([$promptId, $catId]);\n        }\n\n        echo json_encode(['id' => $promptId]);\n    } elseif ($action === 'batch_create') {\n        $data = json_decode(file_get_contents('php://input'), true);\n        $prompts = $data['prompts'] ?? [];\n\n        $pdo->beginTransaction();\n        $inserted = 0;\n        foreach ($prompts as $prompt) {\n            try {\n                $stmt = $pdo->prepare('INSERT INTO prompts (text, difficulty, date) VALUES (?, ?, ?)');\n                $stmt->execute([$prompt['text'], $prompt['difficulty'] ?? 1, time()]);\n                $inserted++;\n            } catch (PDOException $e) {\n            }\n        }\n        $pdo->commit();\n        echo json_encode(['inserted' => $inserted, 'total' => count($prompts)]);\n    } elseif ($action === 'update') {\n        $data = json_decode(file_get_contents('php://input'), true);\n        $id = $data['id'] ?? null;\n        $text = $data['text'] ?? '';\n        $difficulty = $data['difficulty'] ?? 1;\n        $is_active = $data['is_active'] ?? 1;\n        $categories = $data['categories'] ?? [];\n\n        if (!$id) {\n            throw new Exception('ID is required');\n        }\n\n        $pdo->beginTransaction();\n        $stmt = $pdo->prepare('UPDATE prompts SET text = ?, difficulty = ?, is_active = ? WHERE id = ?');\n        $stmt->execute([$text, $difficulty, $is_active, $id]);\n\n        $stmt = $pdo->prepare('DELETE FROM prompt_categories WHERE prompt_id = ?');\n        $stmt->execute([$id]);\n\n        foreach ($categories as $catId) {\n            $stmt = $pdo->prepare('INSERT INTO prompt_categories (prompt_id, category_id) VALUES (?, ?)');\n            $stmt->execute([$id, $catId]);\n        }\n        $pdo->commit();\n        echo json_encode(['success' => true]);\n    } elseif ($action === 'delete') {\n        $id = $_POST['id'] ?? null;\n        if (!$id) {\n            throw new Exception('ID is required');\n        }\n\n        $stmt = $pdo->prepare('DELETE FROM prompts WHERE id = ?');\n        $stmt->execute([$id]);\n        echo json_encode(['success' => true]);\n    } else {\n        throw new Exception('Unknown action');\n    }\n} catch (Exception $e) {\n    http_response_code(400);\n    echo json_encode(['error' => $e->getMessage()]);\n}\n